    function checkErrorMessage () {
        return errorMessage ? true : false;
    }

    function handleTableGeneration () {
        let tempForeignerList = [];
        tempForeignerList.length = foreignerNumber;

        let tempJapaneseList = [];
        tempJapaneseList.length = this.tempJapaneseList;

        let tempTableList = [];
        tempTableList.length = this.tableNumber;

        console.log(tempForeignerList);
        console.log(tempJapaneseList);
        console.log(tempTableList);

    }

    return (
        <div>
            <div className="student-container">
                <h1>List of Attendees</h1>
                <Form>
                    <Container>
                        <Row>
                            <Col>
                                <ShuffleTimeList shuffleList={japaneseNumber} setShuffleList={setJapaneseNumber} participant="japanese"/>
                            </Col>
                            <Col>
                                <ShuffleTimeList shuffleList={foreignerNumber} setShuffleList={setForeignerNumber} participant="foreigner"/>
                            </Col>
                            <Col>
                                <ShuffleTimeDropdown setTableNumber={setTableNumber} />
                            </Col>
                            <Col>
                                <Button onClick={handleTableGeneration()} variant="primary" type="submit">
                                    Submit
                                </Button>
                            </Col>
                        </Row>
                    </Container>
                </Form>
            </div>
        </div>
    )
};

export default ShuffleTimeContainer;
