import Container from 'react-bootstrap/Container';
import ListGroup from 'react-bootstrap/ListGroup'
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';  
import Card from 'react-bootstrap/Card';
import ShuffleTimeCards from './ShuffleTimeCards';

const ShuffleTimeCardContainer = (props) => {

    const { tableMapping, foreignerNumber, japaneseNumber } = props;

    function createTableSlots() {
        let rows = [];
        let innerRow = [];
        let tableMapLength = tableMapping.length;
        let foreignerNumbers = Array.from({length: foreignerNumber}, (v, k) => k+1); 
        let colNumber = Math.floor(tableMapLength / 5);
        let leftoverCol = tableMapping % 5;
        const tableNames = [
            "A", "B", "C", "D", "E", "F", "G", "H", "I", "J"
        ]
        let tableIterator = 0; 

        console.log(colNumber);

        for (let i = 0; i < tableMapLength; ++i) {
            tableMapping[i]
            innerRow.push(
                <Col xs={3}>
                    <ShuffleTimeCards tableName={tableNames[tableIterator]} tableMapping={tableMapping} foreignerNumber={foreignerNumber} japaneseNumber={japaneseNumber} /> 
                </Col>        
            );
            tableIterator++;  

        }

        rows.push(<Row> {innerRow} + </Row>);

        console.log(rows);
        return rows;
    }

    return (
        <div>
            { createTableSlots() }
        </div>
    )
};

export default ShuffleTimeCardContainer;
