import React, { useState } from 'react';
import Button from 'react-bootstrap/Button'
import ListGroup from 'react-bootstrap/ListGroup';
import InputGroup from 'react-bootstrap/InputGroup';    
import FormControl from 'react-bootstrap/FormControl';    
import '../styles/ShuffleTimeList.css';

const ShuffleTimeDropdown = (props) => { 
    const { setTableNumber } = props;

    function updateTableNumber(event) {
        console.log(event);
        setTableNumber(event)
    }

    return (
        <Form.Group controlId="exampleForm.ControlSelect1">
            <Form.Label># of Tables</Form.Label>
            <Form.Control as="select" onChange={updateTableNumber()}>
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
                <option>6</option>
                <option>7</option>
                <option>8</option>
                <option>9</option>
                <option>10</option>
            </Form.Control>
        </Form.Group>
    )
}

export default ShuffleTimeDropdown;
