import ShuffleTimeList from './ShuffleTimeList';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import '../styles/ShuffleTimeContainer.css';
import defaultJson from '../json/ShuffleList.json';

const ShuffleTimeContainer = (props) => {

    const [japaneseList, setJapaneseList] = useState([]);
    const [foreignerList, setForeignerList] = useState([]);
    const [editMode, setEditMode] = useState(false);
    const [errorMessage, setErrorMessage] = useState("");

    useEffect(() => {
        //use default json if it is not empty
        let shuffleJson = defaultJson;
        console.log(shuffleJson);
        if(defaultJson["japanese"] && defaultJson["japanese"].length !== 0) {

            let newStudentList = [];
            for (let i = 0; i < defaultJson.students.length; ++i) {
                newStudentList.push({id: i+1, name: defaultJson.students[i]});
            }
            setStudents(newStudentList);
        }
        else{
            console.log("Japanese json is empty");
        }

    }, []);

