    // }

    //check if errorMessage is not empty
    function checkErrorMessage () {
        return errorMessage ? true : false;
    }

    return (
        <div>
            <div className="student-container">
                <h1>List of Students</h1>
                <Container>
                    <Row>
                        <Col>
                            <ShuffleTimeList shuffleList={japaneseList} setShuffleList={setJapaneseList}/>
                        </Col>
                        <Col>
                            <ShuffleTimeList shuffleList={foreignerList} setShuffleList={setForeignerList}/>
                        </Col>
                    </Row>
                </Container>
            </div>
        </div>
    )
}

export default ShuffleTimeContainer;
