import React, { useState } from 'react';
import Button from 'react-bootstrap/Button'
import ListGroup from 'react-bootstrap/ListGroup';
import InputGroup from 'react-bootstrap/InputGroup';
import '../styles/ShuffleTimeList.css';

const ShuffleTimeList = (props) => { 
    const { shuffleList, setShuffleList } = props;

    function showShuffleList() {
        return shuffleList.map((attendee) => {
            return (
                <InputGroup className="mb-3">
                    <InputGroup.Prepend>
                    <InputGroup.Text id="basic-addon1">@</InputGroup.Text>
                    </InputGroup.Prepend>   
                    <FormControl
                    placeholder="Username"  
                    aria-label="Username"
                    aria-describedby="basic-addon1"
                    />
              </InputGroup>
            );
        })
    }

    function onClickDeleteItem(attendeeId) {
        const updatedShuffleList = shuffleList.filter((attendee) => {
          return attendee.id !== attendeeId;
        })
        setShuffleList(updatedShuffleList);
      }

    return (
        <ListGroup className="shuffle-list">
            {showShuffleList()}
        </ListGroup>
    )
}

export default ShuffleTimeList;
